import React, { useRef, useState } from "react";
import {
  ScrollView,
  Stack,
  Heading,
  useMediaQuery,
  useToast,
} from "native-base";

import Layout from "../../components/layouts/Layout";
import Carousel from "react-native-reanimated-carousel";
import { ComponentWithNavigationProps } from "../../@types/component";
import { Dimensions } from "react-native";
import MainCarouselItem, {
  MainCarouselItemSkeleton,
} from "../../components/custom/MainCarouselItem";
import useSWR, { SWRResponse } from "swr";
import { formatUrl, getImageUrl, getLogoUrl } from "../../helpers/utils";
import { Gallery } from "../../@types/gallery";
import useStore from "../../context/store";
import { maxSmallScreenWidth } from "../../constants/layout";
import SearchBar from "../../components/custom/SearchBar";
import { Product } from "../../@types/product";
import ProductCard, {
  ProductCardSkeleton,
} from "../../components/custom/ProductCard";
import CategoryCard, {
  CategoryCardSkeleton,
} from "../../components/custom/CategoryCard";
import IconCard from "../../components/custom/IconCard";
import { ConditionalSuspense } from "../../components/layouts/ConditionalSuspense";

export default function Home({
  navigation,
  route,
}: ComponentWithNavigationProps) {
  const toast = useToast();
  type ScreenData = {
    services: Gallery[];
    slides: Gallery[];
  };


  const categories = useStore(state => state.configs.categories)

  const renderGalleryUrl = (itemId, currentGallery) =>
    currentGallery.length - 1 > itemId
      ? getImageUrl(`${currentGallery[itemId].image}`)
      : getLogoUrl();


  const { data, error } = useSWR<ScreenData>(
    formatUrl("items/gallery?limit=-1&status=published&sort=-date_created"),
    async (...args: Parameters<typeof fetch>) => {
      try {
        const res = await fetch(...args);
        const resJson = await res.json();
        if (resJson.errors != undefined)
          return toast.show({ title: resJson.errors[0].message });

        const gottenServices = (resJson.data as Gallery[]).filter(
          (galleryItem: Gallery) => galleryItem.group == "service"
        );

        const gottenSlides = (resJson.data as Gallery[]).filter(
          (galleryItem: Gallery) => galleryItem.group == "slider"
        );

        return {
          services: gottenServices,
          slides: gottenSlides,
        }
      } catch (error) {
        console.log(error);
      }
    }
  );

  const nowToSeeProducts = useSWR<Product[]>(
    formatUrl("items/products?limit=10&status=published&sort=-date_created"),
    async (...args: Parameters<typeof fetch>) => {
      try {
        const res = await fetch(...args);
        const resJson = await res.json();
        if (resJson.errors != undefined){
          toast.show({ title: resJson.errors[0].message });
          throw new Error(resJson.errors)
        } 

        console.log(resJson);

        return resJson.data;
      } catch (error) {
        throw new Error('Handled error')
      }
    }
  );

  const [isSmallScreen] = useMediaQuery({ maxWidth: maxSmallScreenWidth });

  const menus = [
    {
      text: "Soldes",
      route: "Home",
      icon: "52a7196b-eea7-4e0f-9aa6-99eee1145ce5",
    },
    {
      text: "Boutiques",
      route: "Home",
      icon: "fa4014a2-9e42-49d2-b6d6-8aa102ea7beb",
    },
    {
      text: "Made in CI",
      route: "Home",
      icon: "ec3ce20b-1145-4e6d-b98c-ae0a897c5785",
    },
    {
      text: "Recommandés",
      route: "Home",
      icon: "f2c80949-cae7-4295-a10e-486273147030?",
    },
  ];

  const screenWidth =
    Dimensions.get("window").width > 1184
      ? 1184
      : Dimensions.get("window").width;

  const mainCarouselRef = useRef<any>({
    prev: () => {},
    next: () => {},
  });

  return (
    <Layout navigation={navigation} route={route}>
      <ScrollView flex={1} height={"100%"} width={"100%"}>
        <>
          {isSmallScreen ? (
            <SearchBar navigation={navigation} route={route} />
          ) : null}

          {data ? (
            <Carousel
              width={screenWidth}
              height={isSmallScreen ? 300 : 500}
              data={data.slides}
              autoPlay={true}
              autoPlayInterval={3000}
              // ref={mainCarouselRef}
              renderItem={({ item }) => {
                const slideId = data.slides.findIndex(
                  (currentItem: Gallery) => item.id == currentItem.id
                );
                return (
                  <MainCarouselItem
                    onPressPrev={
                      typeof mainCarouselRef.current.prev == "function"
                        ? mainCarouselRef.current.prev
                        : () => {}
                    }
                    onPressNext={
                      typeof mainCarouselRef.current.next == "function"
                        ? mainCarouselRef.current.next
                        : () => {}
                    }
                    id={slideId}
                    totalItem={data.slides.length}
                    heading={item.name}
                    imageUri={renderGalleryUrl(slideId, data.slides)}
                    content={item.description}
                    btnText="VOIR PLUS"
                    paddingBottom={2}
                    width="100%"
                    height={isSmallScreen ? 300 : 500}
                    imageBackgroundStyle={!isSmallScreen ? {} : { height: 300 }}
                    containerStyle={!isSmallScreen ? {} : { height: 300 }}
                    btnAction={() => {}}
                  />
                );
              }}
            />
          ) : (
            <MainCarouselItemSkeleton />
          )}

          <Stack
            display={"flex"}
            alignItems={"center"}
            justifyContent={"space-around"}
            flexDirection={"row"}
            paddingTop={2}
            width={"100%"}
          >
            {menus.map((menu) => {
              return (
                <IconCard
                  key={menu.text}
                  isSmallScreen={isSmallScreen}
                  onPress={() => alert(menu.text)}
                  text={menu.text}
                  image={{ uri: getImageUrl(menu.icon) }}
                />
              );
            })}
          </Stack>

          <Stack
            // paddingTop={5}
            display={"flex"}
            alignItems={"center"}
            // alignItems={"center"}
            // width={"100%"}
          >
            <Heading margin={5}> SÉLECTION RAPIDE</Heading>

            <Stack
              alignItems={"center"}
              justifyContent={"flex-start"}
              direction={"row"}
              flexWrap={"wrap"}
            >
              {categories.length == 0
                ? [...Array(8).keys()].map((key) => (
                    <CategoryCardSkeleton
                      isSmallScreen={isSmallScreen}
                      key={key}
                    />
                  ))
                : null}
              {categories.map((item) => (
                <CategoryCard
                  key={item.id}
                  category={item}
                  isSmallScreen={isSmallScreen}
                  navigation={navigation}
                />
              ))}
            </Stack>
          </Stack>

          <Stack flex={1} alignItems={"center"} width={"100%"} height={"auto"}>
            <Heading margin={5}> ACHÈTE DÈS MAINTENANT</Heading>
            <Stack
              alignItems={"center"}
              justifyContent={"flex-start"}
              direction={"row"}
              flexWrap={"wrap"}
              width={"100%"}
            >
              <ConditionalSuspense 
                condition={nowToSeeProducts.data != undefined }
                fallBack={
                  <>
                   {[...Array(8).keys()].map((key) => (
                    <ProductCardSkeleton
                      isSmallScreen={isSmallScreen}
                      key={key}
                    />
                  ))}
                  </>
                }
                >
                <>
                  {/* {nowToSeeProducts.data.map((item: Product) => (
                    <ProductCard
                      key={item.id}
                      isSmallScreen={isSmallScreen}
                      product={item}
                    />
                  ))} */}
                </>
              </ConditionalSuspense>
            </Stack>
          </Stack>
        </>
      </ScrollView>
    </Layout>
  );
}
