import React, { useRef, useState } from "react";
import {
  Text,
  VStack,
  Image,
  ScrollView,
  Pressable,
  Stack,
  Heading,
  useMediaQuery,
  useToast,
  Skeleton,
} from "native-base";



import Layout from "../../components/layouts/Layout";
import Carousel from "react-native-reanimated-carousel";
import { ComponentWithNavigationProps } from "../../@types/component";
import { Dimensions } from "react-native";
import MainCarouselItem from "../../components/custom/MainCarouselItem";
import useSWR from "swr";
import { formatUrl, getImageUrl, getLogoUrl } from "../../helpers/utils";
import { Gallery } from "../../@types/gallery";
import { Category } from "../../@types/category";
import useStore from "../../context/store";
import { Store } from "../../@types/store";
import { maxSmallScreenWidth } from "../../constants/layout";
import SearchBar from "../../components/custom/SearchBar";
import { Product } from "../../@types/product";
import ProductCard from "../../components/custom/ProductCard";


export default function Home({
  navigation,
  route,
}: ComponentWithNavigationProps) {
  const toast = useToast();
  type ScreenData = {
    services: Gallery[],
    slides: Gallery[],
    categories: Category[]
  }
  const [screenData, setScreenData] = useState<ScreenData>(
    {
      services: [],
      slides: [],
      categories: []
    }
  );

  const [nowToSeeProducts, setNowToSeeProducts] = useState<Product[]>([])

  const renderGalleryUrl = (itemId, currentGallery) =>
    currentGallery.length - 1 > itemId
      ? formatUrl(`assets/${currentGallery[itemId].image}`)
      : getLogoUrl();

  useStore.subscribe((state: Store) => {
    if(screenData.categories.length == 0){
      setScreenData({...screenData, categories: state.configs.categories})
    }
  });

  useSWR(
    formatUrl("items/gallery?limit=-1"),
    async (...args: Parameters<typeof fetch>) => {
      try {
        const res = await fetch(...args);
        const resJson = await res.json();
        if (resJson.errors != undefined)
          return toast.show({ title: resJson.errors[0].message });

        const gottenServices = (resJson.data as Gallery[]).filter(
          (galleryItem: Gallery) => galleryItem.group == "service"
        );

        const gottenSlides = (resJson.data as Gallery[]).filter(
          (galleryItem: Gallery) => galleryItem.group == "slider"
        );
        setScreenData(
          {
            ...screenData, 
            services: gottenServices,
            slides: gottenSlides
          }
        )
      } catch (error) {
        console.log(error);
      }
    }
  );

  useSWR(
    formatUrl("items/products"),
    async (...args: Parameters<typeof fetch>) => {
      try {
        const res = await fetch(...args);
        const resJson = await res.json();
        if (resJson.errors != undefined)
          return toast.show({ title: resJson.errors[0].message });

        setNowToSeeProducts(resJson.data)
      } catch (error) {
        console.log(error);
      }
    }
  );

  const [isSmallScreen] = useMediaQuery({ maxWidth: maxSmallScreenWidth });

  const menus = [
    {
      text: "Soldes",
      route: "Home",
      icon: "52a7196b-eea7-4e0f-9aa6-99eee1145ce5",
    },
    {
      text: "Boutiques",
      route: "Home",
      icon: "fa4014a2-9e42-49d2-b6d6-8aa102ea7beb",
    },
    {
      text: "Made in CI",
      route: "Home",
      icon: "ec3ce20b-1145-4e6d-b98c-ae0a897c5785",
    },
    {
      text: "Recommandés",
      route: "Home",
      icon: "f2c80949-cae7-4295-a10e-486273147030?",
    },
  ];

  const screenWidth =
    Dimensions.get("window").width > 1184
      ? 1184
      : Dimensions.get("window").width;

  const mainCarouselRef = useRef<any>({
    prev: () => {},
    next: () => {},
  });

  return (
    <Layout navigation={navigation} route={route}>
      <ScrollView flex={1} height={"100%"} width={"100%"}>
        <>
          {isSmallScreen ? (
            <SearchBar navigation={navigation} route={route} />
          ) : null}


          <VStack
            display={"flex"}
            alignItems={"center"}
            justifyContent={"space-around"}
            flexDirection={"row"}
            paddingTop={2}
            width={"100%"}
          >
            {menus.map((menu) => {
              return (
                <Pressable
                  key={menu.text}
                  onPress={() => navigation.navigate(menu.route)}
                >
                  <VStack flex={1} alignItems={"center"}>
                    <Image
                      width={35}
                      height={35}
                      source={{ uri: getImageUrl(menu.icon) }}
                      alt={menu.text}
                    />
                    <Text
                      textAlign={"center"}
                      fontWeight={"bold"}
                      fontSize={isSmallScreen ? "xs" : "md"}
                    >
                      {menu.text}
                    </Text>
                  </VStack>
                </Pressable>
              );
            })}
          </VStack>

          <Stack
            paddingTop={5}
            flex={1}
            alignItems={"center"}
            width={"100%"}
            height={"auto"}
          >
            <Heading margin={5}> SÉLECTION RAPIDE</Heading>
            <Stack
              alignItems={"center"}
              justifyContent={"flex-start"}
              direction={"row"}
              flexWrap={"wrap"}
            >
              {screenData.categories.map((item) => (
                <Pressable
                  onPress={() =>
                    navigation.navigate("Category", { id: item.id })
                  }
                  flexBasis={isSmallScreen ? "33%" : "25%"}
                  height={isSmallScreen ? 100 : 125}
                  key={item.id}
                >
                  <Stack flex={1} alignItems={"center"}>
                    <Image
                      rounded={50}
                      width={isSmallScreen ? 50 : 70}
                      height={isSmallScreen ? 50 : 70}
                      source={{ uri: getImageUrl(item.thumb) }}
                      alt={item.name}
                    />
                    <Text
                      textAlign={"center"}
                      fontWeight={"bold"}
                      fontSize={isSmallScreen ? "xs" : "md"}
                      paddingLeft={1}
                    >
                      {item.name}
                    </Text>
                  </Stack>
                </Pressable>
              ))}
            </Stack>
          </Stack>


          <Stack
            flex={1}
            alignItems={"center"}
            width={"100%"}
            height={"auto"}
          >
            <Heading margin={5}> ACHÈTE DÈS MAINTENANT</Heading>
            <Stack
              alignItems={"center"}
              justifyContent={"flex-start"}
              direction={"row"}
              flexWrap={"wrap"}
              width={"100%"}
            >
              {nowToSeeProducts.map((item: Product) => (
                <ProductCard key={item.id} isSmallScreen={isSmallScreen} product={item}/>
              ))}
            </Stack>
          </Stack>

        </>
      </ScrollView>
    </Layout>
  );
}
