import {
  Text,
  Stack,
  Image,
  Pressable,
  useMediaQuery,
  Box,
  Skeleton,
} from "native-base";
import React from "react";
import { StyleSheet } from "react-native";
import { Hoverable } from "react-native-web-hover";
import { MAX_SMALL_SCREEN_WIDTH } from "../../constants/layout";
import { sharedStyles } from "../../styles/shared";

type Props = {
  text: string;
  onPress: Function;
  image: any;
};

const ImageCard = ({ text, onPress, image }: Props) => {
  const [isSmallScreen] = useMediaQuery({ maxWidth: MAX_SMALL_SCREEN_WIDTH });
  const square = isSmallScreen == true ? 100 : 120;
  return (
    <Pressable
      flex={1}
      alignItems="center"
      flexBasis={isSmallScreen == true ? "48%" : "24%"}
      width={isSmallScreen == true ? "48%" : "24%"}
      height={square + 10}
      onPress={() => onPress()}
      paddingBottom={2}
    >
      <Hoverable>
        {({ hovered }) => (
          <Pressable
            width={"100%"}
            height={"100%"}
            flex={1}
            alignItems={"center"}
            style={hovered ? sharedStyles.boxBordered : {}}
          >
            <Image
              height={square}
              width={square}
              source={image}
              alt={text}
              resizeMode="contain"
            />
            <Text
              textAlign={"center"}
              fontWeight={"bold"}
              fontSize={isSmallScreen ? "xs" : "md"}
              height={10}
            >
              {text}
            </Text>
          </Pressable>
        )}
      </Hoverable>
    </Pressable>
  );
};

export const ImageCardSkeleton = () => {
  const [isSmallScreen] = useMediaQuery({ maxWidth: MAX_SMALL_SCREEN_WIDTH });
  const square = isSmallScreen == true ? 100 : 120;

  return (
    <Skeleton
      flexBasis={isSmallScreen == true ? "48%" : "24%"}
      width={isSmallScreen == true ? "48%" : "24%"}
      height={square + 10}
      marginBottom={3}
      px="4"
      my="4"
      rounded="md"
    />
  );
};
const styles = StyleSheet.create({});

export default ImageCard;
