type Props =  {
    condition: boolean,
    children: JSX.Element
    fallBackComponent?: JSX.Element
}

export const Invisible = ({condition, children, fallBackComponent}: Props)=>{
    if(condition) return children
    return fallBackComponent != undefined ? fallBackComponent : null
}
