import { Image, Stack, Text, Pressable, Skeleton } from "native-base";
import { StyleSheet } from "react-native";
import React from "react";
import { Product } from "../../@types/product";
import { getImageUrl } from "../../helpers/utils";
import { Hoverable } from "react-native-web-hover";
import { sharedStyles } from "../../styles/shared";

type Props = {
  isSmallScreen?: boolean;
  product: Product;
};

type SkeletonProps = {
  isSmallScreen: boolean
};

const ProductCard = ({ isSmallScreen, product }: Props) => {
  const textWith = isSmallScreen == true ? 138.8 : 190;
  return (
    <Pressable
      flex={1}
      alignItems="center"
      flexBasis={isSmallScreen == true ? "48%" : "24%"}
      width={isSmallScreen == true ? "48%" : "24%"}
      paddingBottom={5}
      height={isSmallScreen == true ? 229.8 : 285.5}
      onPress={() => alert(product.name)}
    >
      <Hoverable>
        {({ hovered }) => (
          <Pressable
            onPress={() => alert(product.name)}
            width={"100%"}
            height={"100%"}
            flex={1}
            alignItems={"center"}
            style={hovered ? sharedStyles.boxBordered : {}}
          >
            <Image
              src={getImageUrl(product.thumb)}
              alt={product.name}
              height={isSmallScreen == true ? 138.8 : 190}
              width={isSmallScreen == true ? 138.8 : 190}
            />
            <Text
              numberOfLines={1}
              ellipsizeMode="tail"
              fontSize={isSmallScreen == true ? "md" : "md"}
              width={textWith}
              textAlign={"center"}
            >
              {product.name}
            </Text>
            <Text fontWeight={"bold"} width={textWith} textAlign={"center"}>
              {new Intl.NumberFormat("fr-FR", {
                style: "currency",
                currency: "XOF",
              }).format(product.price)}
            </Text>
            {product.price_before_promotion != null ? (
              <Text
                style={{
                  textDecorationLine: "line-through",
                  textDecorationStyle: "solid",
                }}
                width={textWith}
                textAlign={"center"}
              >
                {new Intl.NumberFormat("fr-FR", {
                  style: "currency",
                  currency: "XOF",
                }).format(product.price_before_promotion)}
              </Text>
            ) : null}
          </Pressable>
        )}
      </Hoverable>
    </Pressable>
  );
};


export const ProductCardSkeleton = ({isSmallScreen}: SkeletonProps) => (
  <Stack
    flexBasis={isSmallScreen == true ? "48%" : "24%"}
    width={isSmallScreen == true ? "48%" : "24%"}
    paddingBottom={5}
    height={isSmallScreen == true ? 190 : 210}
    flex={1}
    alignItems={"center"}
  >
    <Skeleton
      px="4"
      my="4"
      rounded="md"
      height={"60%"}
      maxHeight={"60%"}
      width={"80%"}
    />
    <Skeleton.Text px="4" width={"80%"} height={"15%"} maxHeight={"15%"} />
  </Stack>
);

const styles = StyleSheet.create({

});

export default ProductCard;
