import {
  AspectRatio,
  Box,
  Image,
  Center,
  Stack,
  Text,
  Heading,
  HStack,
} from "native-base";
import React from "react";
import { Product } from "../../@types/product";
import { getImageUrl } from "../../helpers/utils";

type Props = {
  isSmallScreen?: boolean,
  product: Product
}
const ProductCard = ({isSmallScreen, product}: Props) => (
  <Stack flex={1} alignItems="center" width={isSmallScreen == true ? '48%' : '24%'} paddingBottom={5} height={isSmallScreen == true ? 285.5 : 229.8}>
      <Image src={getImageUrl(product.thumb)} alt={product.name} height={isSmallScreen == true ? 138.8 : 223.5} width={isSmallScreen == true ? 138.8 : 223.5}/>
  </Stack>
);

export default ProductCard;
