import { Text, Stack, Image, Pressable } from "native-base";
import React from "react";
import { StyleSheet } from "react-native";
import { Category } from "../../@types/category";
import { getImageUrl } from "../../helpers/utils";

type Props = {
  category: Category;
  navigation: any;
  isSmallScreen?: boolean
};

const CategoryCard = ({ category, navigation, isSmallScreen }: Props) => {
  return (
    <Pressable
      onPress={() => navigation.navigate("Category", { id: category.id })}
      flexBasis={isSmallScreen ? "33%" : "25%"}
      height={isSmallScreen ? 100 : 125}
      key={category.id}
    >
      <Stack flex={1} alignItems={"center"}>
        <Image
          rounded={50}
          width={isSmallScreen ? 50 : 70}
          height={isSmallScreen ? 50 : 70}
          source={{ uri: getImageUrl(category.thumb) }}
          alt={category.name}
        />
        <Text
          textAlign={"center"}
          fontWeight={"bold"}
          fontSize={isSmallScreen ? "xs" : "md"}
          paddingLeft={1}
        >
          {category.name}
        </Text>
      </Stack>
    </Pressable>
  );
};
const styles = StyleSheet.create({
  imageBackground: {},
  text: {
    textAlign: "center",
  },
});

export default CategoryCard;
