import { Text, Stack, Image, Pressable } from "native-base";
import React from "react";
import { StyleSheet } from "react-native";
import { Hoverable } from "react-native-web-hover";

type Props = {
  text: string,
  onPress: Function,
  isSmallScreen?: boolean,
  image: any
};

const IconCard = ({ text, onPress, image, isSmallScreen }: Props) => {
  return (
    <Pressable
        key={text}
        onPress={onPress()}
    >
      <Hoverable>
        {({ hovered }) => (
            <Stack flex={1} alignItems={"center"}>
              <Image
                width={35}
                height={35}
                source={image}
                alt={text}
              />
              <Text
                textAlign={"center"}
                fontWeight={"bold"}
                fontSize={isSmallScreen ? "xs" : "md"}
              >
                {text}
              </Text>
            </Stack>
        )}
      </Hoverable>
    </Pressable>
  );
};

const styles = StyleSheet.create({
  boxBordered: {
    shadowColor: "#000",
    shadowOffset: {
      width: 1,
      height: 4,
    },
    shadowOpacity: 0.5,
    shadowRadius: 10,
    elevation: 5,
  },
});

export default IconCard;
