type Props = {
  condition: boolean;
  children: JSX.Element;
  fallBack?: JSX.Element | Element;
};

export const ConditionalSuspense = ({
  condition,
  children,
  fallBack,
}: Props) => {
  if (condition) return children;
  return fallBack != undefined ? fallBack : null;
};
