import { Text, Stack, Image, Pressable, Skeleton } from "native-base";
import React from "react";
import { StyleSheet } from "react-native";
import { Hoverable } from "react-native-web-hover";
import { Category } from "../../@types/category";
import { getImageUrl } from "../../helpers/utils";
import { sharedStyles } from "../../styles/shared";

type Props = {
  category: Category;
  navigation: any;
  isSmallScreen?: boolean;
};

type SkeletonProps = {
  isSmallScreen?: boolean;
};

const CategoryCard = ({ category, navigation, isSmallScreen }: Props) => {
  return (
    <Pressable
      onPress={() => navigation.navigate("Category", { id: category.id })}
      flexBasis={isSmallScreen ? "33%" : "25%"}
      height={isSmallScreen ? 100 : 125}
      key={category.id}
    >
      <Hoverable>
        {({ hovered }) => (
          <Stack
            flex={1}
            alignItems={"center"}
            style={hovered ? sharedStyles.boxBordered : {}}
          >
            <Image
              rounded={50}
              width={isSmallScreen ? 50 : 70}
              height={isSmallScreen ? 50 : 70}
              source={{ uri: getImageUrl(category.thumb) }}
              alt={category.name}
            />
            <Text
              textAlign={"center"}
              fontWeight={"bold"}
              fontSize={isSmallScreen ? "xs" : "md"}
              paddingLeft={1}
            >
              {category.name}
            </Text>
          </Stack>
        )}
      </Hoverable>
    </Pressable>
  );
};

export const CategoryCardSkeleton = ({ isSmallScreen }: SkeletonProps) => (
  <Skeleton
    flexBasis={isSmallScreen ? "33%" : "25%"}
    width={isSmallScreen ? 50 : 70}
    height={isSmallScreen ? 50 : 70}
    px="4"
    my="4"
    rounded="md"
  />
);

const styles = StyleSheet.create({
  boxBordered: {
    shadowColor: "#000",
    shadowOffset: {
      width: 1,
      height: 4,
    },
    shadowOpacity: 0.5,
    shadowRadius: 10,
    elevation: 5,
  },
});

export default CategoryCard;
