import {
  Image,
  Stack,
  Text,
  Pressable
} from "native-base";
import { StyleSheet } from "react-native";
import React from "react";
import { Product } from "../../@types/product";
import { getImageUrl } from "../../helpers/utils";

type Props = {
  isSmallScreen?: boolean;
  product: Product;
};

const ProductCard = ({ isSmallScreen, product }: Props) => (
  <Pressable
    flex={1}
    alignItems="center"
    flexBasis={isSmallScreen == true ? "48%" : "24%"}
    width={isSmallScreen == true ? "48%" : "24%"}
    paddingBottom={5}
    height={isSmallScreen == true ? 229.8 : 285.5}
    onPress={()=> alert(product.name)}
  >
    <Image
      src={getImageUrl(product.thumb)}
      alt={product.name}
      height={isSmallScreen == true ? 138.8 : 200.5}
      width={isSmallScreen == true ? 138.8 : 200.5}
    />
    <Text
      numberOfLines={1}
      ellipsizeMode="tail"
      fontSize={isSmallScreen == true ? "md" : "md"}
      width={"95%"}
    >
      {product.name}
    </Text>
    <Text fontWeight={"bold"}>
      {new Intl.NumberFormat("fr-FR", {
        style: "currency",
        currency: "XOF",
      }).format(product.price)}
    </Text>
    {product.price_before_promotion != null ? (
      <Text
        style={{
          textDecorationLine: "line-through",
          textDecorationStyle: "solid",
        }}
      >
        {new Intl.NumberFormat("fr-FR", {
          style: "currency",
          currency: "XOF",
        }).format(product.price_before_promotion)}
      </Text>
    ) : null}
  </Pressable>
);

const styles = StyleSheet.create({
  boxBordered: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4.84,

    elevation: 5,
  },
});

export default ProductCard;
