type Props = {
  condition: boolean;
  children: JSX.Element;
  fallBack?: JSX.Element;
};

export const Invisible = ({
  condition,
  children,
  fallBack,
}: Props) => {
  if (condition) return children;
  return fallBack != undefined ? fallBack : null;
};
