import {
  AspectRatio,
  Box,
  Image,
  Center,
  Stack,
  Text,
  Heading,
  HStack,
} from "native-base";
import React from "react";

type Props = {
  isSmallScreen?: boolean
}
const ProductCard = ({isSmallScreen}: Props) => (
  <Stack flex={1} alignItems="center" width={isSmallScreen == true ? '48%' : '24%'} paddingBottom={5} height={isSmallScreen == true ? 285.5 : 229.8}>
      <Image></Image>
  </Stack>
);

export default ProductCard;
