import React from "react";
import { StyleSheet } from "react-native";
import { Text, Stack, Image, Pressable, Skeleton } from "native-base";
import { Hoverable } from "react-native-web-hover";
import { Category } from "../../@types/category";
import { getImageUrl } from "../../helpers/utils";
import { sharedStyles } from "../../styles/shared";

type Props = {
  category: Category;
  navigation: any;
  onPress: Function;
  isSmallScreen?: boolean;
};

type SkeletonProps = {
  isSmallScreen?: boolean;
};

const ImageCard = ({ category, navigation, isSmallScreen }: Props) => {
  return (
    <Pressable
      onPress={() => navigation.onPress()}
      flexBasis={isSmallScreen ? "33%" : "25%"}
      height={isSmallScreen ? 100 : 125}
      key={category.id}
    >
      <Hoverable>
        {({ hovered }) => (
          <Stack
            flex={1}
            alignItems={"center"}
            style={hovered ? sharedStyles.boxBordered : {}}
          >
            <Image
              rounded={50}
              width={isSmallScreen ? 50 : 70}
              height={isSmallScreen ? 50 : 70}
              source={{ uri: getImageUrl(category.thumb) }}
              alt={category.name}
            />
            <Text
              textAlign={"center"}
              fontWeight={"bold"}
              fontSize={isSmallScreen ? "xs" : "md"}
              paddingLeft={1}
              height={isSmallScreen ? 4 : 7}
              numberOfLines={1}
              ellipsizeMode="tail"
            >
              {category.name}
            </Text>

          </Stack>
        )}
      </Hoverable>
    </Pressable>
  );
};

export const ImageCardSkeleton = ({ isSmallScreen }: SkeletonProps) => (
  <Skeleton
    flexBasis={isSmallScreen ? "33%" : "25%"}
    width={isSmallScreen ? 50 : 70}
    height={isSmallScreen ? 50 : 70}
    px="4"
    my="4"
    rounded="md"
  />
);

const styles = StyleSheet.create({

});

export default ImageCard;
